import React, { useEffect, useState } from "react";
import axios from 'axios'
import iconOpen from './assets/icons/open.svg'
import imagePerfil from './assets/images/perfil.jpg'
const host = 'http://localhost:8080'
const hostClient = 'http://localhost:3000'

export default function Home () {
    const [form, setForm] = useState({
        userName: '',
        message: ''
    })

    const [chats, setChats] = useState([])

    useEffect(() => {
        if (localStorage.getItem('userName') !== null) {
            axios.post(`${host}/getMyChats`, {
                userName: localStorage.getItem('userName')
            }).then((res) => {
                setChats(res.data)
            }).catch((error) => console.log(error))
        } else {
            window.location.href = `${hostClient}/signin`
        }
    },[])

    const openModal = (msg) => {
        const ctrModal = document.querySelector('.ctr-modal')

        ctrModal.classList.remove('close')
        document.querySelector('.modal p').innerHTML = msg

        document.addEventListener('keydown', (key) => {
            key = key.key

            if (key === 'Enter' || key === 'Escape') {
                closeModal()
            }
        })
    }

    const closeModal = () => {
        document.querySelector('.ctr-modal').classList.add('close')
        document.querySelector('.ctr-modal button').focus()
    }

    const f_closeAddUser = () => {
        document.querySelector('.modal-addUser').classList.add('close')
    }

    const f_handleInputs = (e) => {
        if (e.value !== '' && e.value !== ' ') {
            setForm({...form, [e.name]: e.value})
        }
    }

    const validate = () => {
        let isValidate = true

        setForm({userName: form.userName.trim(), message: form.message.trim()})

        const regexText = new RegExp(
            /^[a-zA-Z0-9]$/
        )

        if (regexText.test(form.userName)) {
            openModal(`Por favor não use  careceres especiais no campo userName`)
            isValidate = false
        }

        if (form.userName === '') {
            openModal(`Por favor preencha o campo userName`)
            isValidate = false
        }

        if (regexText.test(form.message)) {
            openModal(`Por favor não use careceres especiais no campo mensagem`)
            isValidate = false
        }

        if (form.message === '') {
            openModal(`Por favor escreva algo legal para iniciar a conversa`)
            isValidate = false
        }

        return isValidate
    }

    const f_addUser = () => {
        console.log(chats)
        if (validate()) {
            document.querySelector('.wrapper').classList.remove('close')
            document.querySelector('.blur').classList.remove('none')

            axios.post(`${host}/addChat`, {
                friendName: form.userName,
                userName: localStorage.getItem('userName'),
                message: form.message,
            }).then((res) => {
                    document.querySelector('.wrapper').classList.add('close')
                    document.querySelector('.blur').classList.add('none')

                    if (res.data !== null) {
                        f_closeAddUser()
                        openModal('Amigo adicionado com sucesso! acabamos de adicionar um novo chat')
                    } else {
                        openModal('Usurário não encontrado, veja se há erro na digitação')
                    }
                }).catch((error) => {
                    console.log(error)
                })
        }
    }

    return (
    <div className="home">
        <div className="blur none"></div>

        <div className="wrapper close">
            <div className="circle"></div>
            <div className="circle"></div>
            <div className="circle"></div>
        </div>

        <div className="ctr-modal close">
            <div className="modal">
                <div className="ctr-notice">
                    <h3 className="notice">Aviso:</h3>
                    <p></p>
                </div>

                <button className="close button-small" id="buttonModalClose" onClick={() => {closeModal()}}>Fechar</button>
            </div>
        </div>

        <div className="modal-addUser close">
            <h3>Adicionar amigo</h3>

            <div className="ctr-inputs">
                <div className="ctr-input">
                    <label htmlFor="userName">UserName:</label>
                    <input 
                        type='text' 
                        id="userName" 
                        name="userName" 
                        value={form.userName}
                        placeholder='Digite o userName do seu amigo'
                        onChange={(e) => {f_handleInputs(e.target)}}
                    />
                </div>

                <div className="ctr-input">
                    <label htmlFor="userName">Mensagem:</label>

                    <textarea
                        id="message" 
                        name="message" 
                        value={form.message}
                        placeholder='Escreva algo legal para iniciar a conversa'
                        onChange={(e) => {f_handleInputs(e.target)}}
                    ></textarea>
                </div>
            </div>

            <button onClick={() => {f_addUser()}}>Adicionar</button>
            <button 
                className="button-cancel" 
                onClick={() => {f_closeAddUser()}}
            >Cancelar</button>
        </div>

        <div className="perfil">
            <img src={imagePerfil} className="picture-large" alt="minha foto de perfil"/>
            <p className="body-large">{localStorage.getItem('userName')}</p>

            <button>Editar dados</button>
            <button 
                className="addUser"
                onClick={() => {document.querySelector('.modal-addUser').classList.remove('close')}}
            >Adicionar amigo</button>
        </div>

        <div className="chats">
            {typeof(chats) !== 'undefined' && chats.map(chat => {
                return (
                <div className="chat" key={`1${chat.name}`}>
                    <div className="ctr-infoUser" key={chat.name}>
                        <img key={chat.name} src='#' className="picture-small" alt="foto do suário Joh"/>

                        <div key={chat.name} className="infoUser">
                            <p key={chat.name}>{chat.name}</p>
                            <p key={chat.name} className="body-small msg">{chat.message.text}</p>
                        </div>
                    </div>  

                    <div className="ctr-infoChat" key={chat.name}>
                        <span key={chat.name} className="caption-small">{chat.message.date}</span>

                        <div key={chat.name} className="infoChat">
                            <div key={chat.name} className="ctr-messageCount">
                                <span key={chat.name} className="messageCount caption-small">2</span>
                            </div>

                            <img key={chat.name} src={iconOpen} className="openChat" alt="abrir o chat" />
                        </div>
                    </div>
                </div>
                )
            })}
        </div>
    </div>
    )
}
