        axios.post(`${host}/getChat`, {
            chatName: localStorage.getItem('chatName'),
            userName: localStorage.getItem('userName')
        }).then((res) => {
            setHistory(res.data)
            console.log(res.data)
        }).catch((error) => console.log(error))
    }, [])

