    <div className="perfil">
        <img src={imagePerfil} className="picture-large" alt="minha foto de perfil"/>
        <p className="body-large">{localStorage.getItem('chatName')}</p>
        <p>Online</p>

        <button 
            className="block"
            onClick={() => {blockUser()}}
