import React from "react";
import { Routes, Route, Link } from 'react-router-dom'
import { io } from 'socket.io-client'
import Home from './components/Home'
import SignIn from './components/SignIn'
import SignUp from './components/SignUp'

const socket = io("http://localhost:8080")
const hostClient = 'http://localhost:3000'


export default function App () {
	const f_logoff = () => {
		localStorage.clear()
		window.location.href = `${hostClient}/signin`
	}

	const f_returnLinks = () => {
		if (window.location.href === `${hostClient}/signin` || window.location.href === `${hostClient}/signup`) {
			return (
				<>
					<Link to='/signin'>Sign-in</Link>
					<Link to='/signup'>Sign-up</Link>
				</>
			)
		} else {
			return (
				<button className="link" onClick={() => {f_logoff()}}>Sair</button>
			)
		}
	}
    return (
    <>
		<header>
			<div className="ctr-logo">
			<h1 class="S1PPyQ" style="color: rgb(255, 255, 255); font-weight: 400; font-style: normal; text-decoration: none;">Bate</h1>
			</div>

			<nav>
				{f_returnLinks()}
			</nav>
		</header>

		<main>
			<Routes>
				<Route path="/" element={<Home/>}></Route>
				<Route path="/signin" element={<SignIn/>}></Route>
				<Route path="/signup" element={<SignUp/>}></Route>
			</Routes>
		</main>
    </>
    )
}
