import React, { useEffect, useState } from "react";
import iconOpen from './assets/icons/open.svg'
import sendMessage from './assets/icons/iconSend.svg'
import iconEmoji from './assets/icons/iconEmoji.svg'
import axios from 'axios'
const host = 'http://localhost:7070'


export default function Chat () {
    const [history, setHistory] = useState()

    useEffect(() => {        
        axios.post(`${host}/getHistory`, {
            chatName: localStorage.getItem('chtName'),
            userName: localStorage.getItem('userName')
        }).then((res) => {
            setHistory(res.data)
        }).catch((error) => console.log(error))
    }, [])
    return (
    <div className="currentlyOpenChat">
        <div className="header">
            <button className="buttonClose" onClick={() => {
                document.querySelector('main').classList.add('opacity-0')

                setTimeout(() => {
                    window.location.href = '/'
                }, 1000)
            }}><img src={iconOpen} alt="ícone voltar"/></button>
        </div>

        <div className="messages">
                <div className="ctr-person-messages">
                    {typeof(history) !== 'undefined' && history.map((message) => {
                        if (message.message.userName === localStorage.getItem('userName')) {
                            return (
                            <div className="person-messages" key={`person-message${message.name}`}>
                                <p className="message body-small" key={`person-message${message.name}`}>{message.text}</p>
                                <span className="date-message caption-small" key={`person-message${message.name}`}>{message.date}</span>
                            </div>
                            )
                        }
                    })}
                </div>

                <div className="ctr-your-messages">
                {typeof(history) !== 'undefined' && history.map((message) => {
                        if (message.message.userName === localStorage.getItem('userName')) {
                            return (
                            <div className="your-message" key={`person-message${message.name}`}>
                                <p className="message body-small" key={`person-message${message.name}`}>{message.text}</p>
                                <span className="date-message caption-small" key={`person-message${message.name}`}>{message.date}</span>
                            </div>
                            )
                        }
                    })}
                    <div className="your-message">
                        <p className="message body-small">Lorem ipsum dolor sit amet consectetur, adipisicing elit. Eius doloribus veritatis magni consequuntur, soluta nihil, perferendis nobis modi delectus dolorem nisi consectetur dignissimos voluptatibus repellendus ipsam iste quisquam ut repudiandae!</p>
                        <span className="date-message caption-small">22:22</span>
                    </div>
                </div>
        </div>

        <div className="entrance-area">
            <button className="button-emoji"><img src={iconEmoji} alt='ícone emoji'/></button>
            <input type='text' className="input-message"/>
            <button className="send-message" onClick={() => {console.log('opa mam')}}><img src={sendMessage} alt="botão enviar message"></img></button>
        </div>
    </div>
    )
}
