import React, { useEffect, useState } from "react";
import iconOpen from './assets/icons/open.svg'
import sendMessage from './assets/icons/iconSend.svg'
import iconEmoji from './assets/icons/iconEmoji.svg'
import axios from 'axios'
const host = 'http://localhost:7070'


export default function Chat () {
    const [history, setHistory] = useState()
    const [messageText, setMessageText] = useState()

    useEffect(() => {        
        axios.post(`${host}/getHistory`, {
            chatName: localStorage.getItem('chatName'),
            userName: localStorage.getItem('userName')
        }).then((res) => {
            setHistory(res.data)
        }).catch((error) => console.log(error))
    }, [])

    const f_sendMessage = () => {
        if (validate()) {

        }
    }

    const validate = () => {
        let isValidate = true

        setForm({
            userName: form.userName.trim(),
            password: form.password.trim()
        })

        const regexText = new RegExp(
            /^[a-zA-Z0-9]$/
        )

        if (regexText.test(form.userName)) {
            openModal(`Por favor não use  careceres especiais no campo userName`)
            isValidate = false
        }

        if (regexText.test(form.password)) {
            openModal(`Por favor não use  careceres especiais no campo na sua senha`)
            isValidate = false
        }

        if (form.userName === '') {
            openModal(`Por favor preencha o campo YserName`)
            isValidate = false
        }

        if (form.password.length < 8) {
            openModal(`Por favor Crie uma senha com pelo menos 8 dígitos`)
            isValidate = false
        }

        return isValidate
    }

    const f_handleInputs = (e) => {
        if (e.value !== '' && e.value !== ' ') {
            setForm({...form, [e.name]: e.value})
        }
    }

    return (
    <div className="currentlyOpenChat">
        <div className="header">
            <button className="buttonClose" onClick={() => {
                document.querySelector('main').classList.add('opacity-0')

                setTimeout(() => {
                    window.location.href = '/'
                }, 1000)
            }}><img src={iconOpen} alt="ícone voltar"/></button>
        </div>

        <div className="messages">
            <div className="ctr-person-messages">
                {typeof(history) !== 'undefined' && history.map((message) => {
                    if (message.user !== localStorage.getItem('userName')) {
                        return (
                        <div className="person-message" key={`person-message-${message.name}`}>
                            <p className="message body-small" key={`messag=e${message.name}`}>{message.text}</p>
                            <span className="date-message caption-small" key={`date-${message.name}`}>{message.date}</span>
                        </div>
                        )
                    }
                })}
            </div>

            <div className="ctr-your-messages">
                {typeof(history) !== 'undefined' && history.map((message) => {
                    if (message.user === localStorage.getItem('userName')) {
                        return (
                        <div className="your-message" key={`your-message-${message.name}`}>
                            <p className="message body-small" key={`message-${message.name}`}>{message.text}</p>
                            <span className="date-message caption-small" key={`date-${message.name}`}>{message.date}</span>
                        </div>
                        )
                    }
                })}
            </div>
        </div>

        <div className="entrance-area">
            <button className="button-emoji"><img src={iconEmoji} alt='ícone emoji'/></button>
            <input 
                type='text' 
                className="input-message" 
                placeholder="escreva sua messagem aqui"
                onChange={(e) => {f_handleInputs(e)}}
            />
            <button className="send-message" onClick={() => {f_sendMessage()}}><img src={sendMessage} alt="botão enviar message"></img></button>
        </div>
    </div>
    )
}
